mapper.select:
SELECT f.id, f.file_name, u.account, ff.coname, f.author_user uname, f.last_edit_user lname, f.create_date, f.update_date FROM f_pc_file f INNER JOIN f_user_account u ON f.uid = u.id INNER JOIN f_user_customer c ON c.uid=u.id INNER JOIN f_firm ff ON c.fid=ff.id WHERE 1=1
AND u.account LIKE CONCAT('%',#{name},'%')
AND f.create_date BETWEEN #{beginDate} AND #{endDate}
AND f.update_date BETWEEN #{beginDate} AND #{endDate}
ORDER BY f.update_date DESC

SELECT p.fid, p.mesh_name, p.mesh_id, p.file_version, u.account, f.author_user uname, f.last_edit_user lname, f.file_path, p.update_date FROM f_pc_file f INNER JOIN f_pc_info p ON f.id=p.fid INNER JOIN f_user_account u ON f.uid = u.id WHERE p.fid = #{id}
AND p.mesh_name LIKE CONCAT('%',#{name},'%')
AND p.update_date BETWEEN #{beginDate} AND #{endDate}
ORDER BY p.update_date DESC
