mapper.select:
SELECT t1.id,t1.name,count(t3.id) as meshNum,t2.account as account,DATE_FORMAT(t1.create_date,'%Y-%m-%d %H:%i:%s') AS create_date,DATE_FORMAT(t1.update_date,'%Y-%m-%d %H:%i:%s') AS update_date FROM
(SELECT * FROM alink.f_project WHERE uid IN
    #{item}
    ) AS t1
INNER JOIN alink.f_user_account AS t2 ON t1.uid = t2.id INNER JOIN alink.f_mesh AS t3 ON t1.id = t3.project_id
GROUP BY t3.project_id ORDER BY t1.create_date DESC
