mapper.select:
SELECT
FROM f_group g INNER JOIN f_mesh m ON m.id = g.mid LEFT JOIN (SELECT COUNT(1) lcount,gid FROM f_light GROUP BY gid) l ON g.id=l.gid WHERE 1=1
AND m.mname LIKE CONCAT('%',#{mname},'%')
AND m.mesh_id LIKE CONCAT('%',#{meshId},'%')
AND g.gname LIKE CONCAT('%',#{gname},'%')
AND g.pid=#{pid}
AND m.id=#{mid}
AND m.project_id=#{projectId}
ORDER BY lcount DESC

mapper.delete:
DELETE FROM f_group
WHERE id IN

    #{id}

mapper.update:
UPDATE f_group SET pid=#{pid},update_date=NOW()
WHERE id IN

    #{id}
