mapper.select:
SELECT
FROM f_light l INNER JOIN f_mesh m ON m.id = l.mid LEFT JOIN f_place pl ON pl.id=l.pid LEFT JOIN f_light_setting ls ON ls.lid=l.id WHERE 1=1
AND m.mname LIKE CONCAT('%',#{mname},'%')
AND m.mesh_id LIKE CONCAT('%',#{meshId},'%')
AND l.lname LIKE CONCAT('%',#{lname},'%')
AND l.lmac LIKE CONCAT('%',#{lmac},'%')
AND l.pid=#{pid}
AND l.mid=#{mid}
AND l.gid=#{gid}
AND m.project_id=#{projectId}
GROUP BY l.id

mapper.delete:
DELETE FROM f_light
WHERE id IN

    #{id}

mapper.update:
UPDATE f_light SET gid=#{gid},update_date=NOW()
WHERE id IN

    #{id}
