mapper.select:
SELECT
FROM f_light l INNER JOIN f_light_setting ls ON ls.lid=l.id INNER JOIN f_mesh m ON m.id = l.mid WHERE 1=1
AND m.mname LIKE CONCAT('%',#{mname},'%')
AND m.mesh_id LIKE CONCAT('%',#{meshId},'%')
AND g.gname LIKE CONCAT('%',#{gname},'%')
AND g.pid=#{pid}
AND m.id=#{mid}
AND m.project_id=#{projectId}
ORDER BY l.mid DESC

mapper.delete:
DELETE FROM f_light
WHERE id IN

    #{id}

mapper.update:
UPDATE f_light SET gid=#{gid},update_date=NOW()
WHERE id IN

    #{id}
